import React, { useState, useEffect } from "react";
import Card from "../components/Card";
import { supabase } from "../client";

const ReadPosts = (props) => {
  const [posts, setPosts] = useState([]);
  const [sortCriteria, setSortCriteria] = useState("created_at");

  useEffect(() => {
    const fetchPosts = async () => {
      const { data, error } = await supabase
        .from("Posts")
        .select()
        .order(sortCriteria, { ascending: sortCriteria !== "vote" });

      if (error) {
        console.error("Error fetching posts:", error);
      } else {
        setPosts(data);
      }
    };

    fetchPosts();
  }, [sortCriteria]);

  const handleSortByTime = () => {
    setSortCriteria("created_at");
  };

  const handleSortByVotes = () => {
    setSortCriteria("vote");
  };

  return (
    <div className="ReadPosts">
      <div className="sortButtons">
        <button onClick={handleSortByTime}>Sort by Time</button>
        <button onClick={handleSortByVotes}>Sort by Votes</button>
      </div>
      <div className="posts">
        {posts && posts.length > 0 ? (
          posts.map((post) => (
            <Card
              key={post.id}
              id={post.id}
              timestamp={post.created_at}
              oldMajor={post.oldMajor}
              newMajor={post.newMajor}
              vote={post.vote}
            />
          ))
        ) : (
          <h2>No Posts Yet</h2>
        )}
      </div>
    </div>
  );
};

export default ReadPosts;
