import React from "react";
import { useState } from "react";
import "./Card.css";
import more from "./more.png";
import { Link } from "react-router-dom";
import { supabase } from "../client";

const Card = (props) => {
  const [count, setCount] = useState(props.vote);

  const updateCount = async (event) => {
    event.preventDefault();

    // Optimistically update the count
    setCount((prevCount) => prevCount + 1);

    // Make the API call to update the vote count in the database
    const { error } = await supabase
      .from("Posts")
      .update({ vote: count + 1 })
      .eq("id", props.id);

    if (error) {
      // If there's an error, revert the count
      setCount((prevCount) => prevCount - 1);
      console.error("Error updating vote count:", error);
    }
  };

  return (
    <div className="Card">
      <Link to={"edit/" + props.id}>
        <img className="moreButton" alt="edit button" src={more} />
      </Link>
      <h2 className="oldMajor">{"Original Major: " + props.oldMajor}</h2>
      <h2 className="newMajor">{"New Major: " + props.newMajor}</h2>
      <button className="vote" onClick={updateCount}>
        {"üëç votes: " + count}
      </button>
    </div>
  );
};

export default Card;
