import React, { useState } from "react";
import "./CreatePost.css";
import { supabase } from "../client";

const CreatePost = () => {
  const [post, setPost] = useState({
    oldMajor: "",
    newMajor: "",
    oldSchool: "",
    newSchool: "",
    motive: "",
    challenge: "",
    advice: "",
    more: "",
    vote: 0,
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setPost((prev) => {
      return {
        ...prev,
        [name]: value,
      };
    });
  };

  const createPost = async (event) => {
    event.preventDefault();

    console.log("Submitting post:", post);

    const { data, error } = await supabase
      .from("Posts")
      .insert({
        oldMajor: post.oldMajor,
        newMajor: post.newMajor,
        oldSchool: post.oldSchool,
        newSchool: post.newSchool,
        motive: post.motive,
        challenge: post.challenge,
        advice: post.advice,
        more: post.more,
        vote: post.vote,
      })
      .select();

    if (error) {
      console.error("Error creating post:", error);
    } else {
      console.log("Post created successfully:", data);
      window.location = "/";
    }
  };

  return (
    <div>
      <form onSubmit={createPost}>
        <label for="oldMajor">Original Major</label>
        <br />
        <input
          type="text"
          id="oldMajor"
          name="oldMajor"
          onChange={handleChange}
        />
        <br />
        <br />

        <label for="oldSchool">School</label>
        <br />
        <input
          type="text"
          id="oldSchool"
          name="oldSchool"
          onChange={handleChange}
        />
        <br />
        <br />

        <label for="newMajor">New Major</label>
        <br />
        <input
          type="text"
          id="newMajor"
          name="newMajor"
          onChange={handleChange}
        />
        <br />
        <br />

        <label for="newSchool">New School</label>
        <br />
        <input
          type="text"
          id="newSchool"
          name="newSchool"
          onChange={handleChange}
        />
        <br />
        <br />

        <label for="motive">Motivation for Change</label>
        <br />
        <textarea
          id="motive"
          name="motive"
          rows="4"
          cols="50"
          onChange={handleChange}
        />
        <br />
        <br />

        <label for="challenge">Challenges Encountered</label>
        <br />
        <textarea
          id="challenge"
          name="challenge"
          rows="4"
          cols="50"
          onChange={handleChange}
        />
        <br />
        <br />

        <label for="advice">Advice for Others</label>
        <br />
        <textarea
          id="advice"
          name="advice"
          rows="4"
          cols="50"
          onChange={handleChange}
        />
        <br />
        <br />

        <label for="more">More to Share</label>
        <br />
        <textarea
          id="more"
          name="more"
          rows="4"
          cols="50"
          onChange={handleChange}
        />
        <br />
        <br />

        <input type="submit" value="Submit" />
      </form>
    </div>
  );
};

export default CreatePost;
